@require_POST
@csrf_protect
@never_cache
def log(request):
    """Gestion scurise de la connexion avec valeurs par dfaut"""
    # Valeur par dfaut si le paramtre n'est pas dfini
    max_attempts = getattr(settings, 'MAX_LOGIN_ATTEMPTS', 5)
    
    # Initialisation du compteur de tentatives
    bad_log = request.session.get('bad_log', 0) + 1
    request.session['bad_log'] = bad_log
    request.session['try_log'] = True
    
    # Protection contre les attaques par force brute
    if bad_log > max_attempts:
        logger.warning(f"Trop de tentatives de connexion depuis l'IP {request.META.get('REMOTE_ADDR')}")
        messages.error(request, "Trop de tentatives. Veuillez ressayer plus tard ou contacter l'administrateur.")
        return redirect("account_locked")
    
    # Validation des entres
    email = request.POST.get('email', '').strip()
    password = request.POST.get('password', '').strip()
    
    if not email or not password:
        messages.error(request, "L'email et le mot de passe sont requis")
        return redirect("/register/")
    
    # Recherche et authentification de l'utilisateur
    authenticated_user = None
    matching_users = User.objects.filter(email=email)
    
    for user in matching_users:
        if authenticate(username=user.username, password=password):
            authenticated_user = user
            break
    
    if not authenticated_user:
        messages.error(request, "Identifiants incorrects")
        return redirect("/register/")
    
    # Connexion russie
    login(request, authenticated_user)
    request.session['bad_log'] = 0  # Rinitialisation du compteur
    
    # Journalisation de la connexion
    logger.info(f"Connexion russie pour {authenticated_user.email} depuis {request.META.get('REMOTE_ADDR')}")
    
    # Redirection en fonction du profil
    if hasattr(authenticated_user, 'personnel'):
        personnel = authenticated_user.personnel
        if hasattr(personnel, 'actual_institution') and personnel.actual_institution:
            request.session['personnel_id'] = personnel.id
            return redirect('dashboard')
        return redirect('boutique')
    
    messages.info(request, "Veuillez contacter l'administrateur pour obtenir l'accs")
    return redirect('/bad_auth/')

logger = logging.getLogger(__name__)

@require_GET
@never_cache
def logout_view(request):
    """Dconnexion scurise avec gestion des paramtres manquants"""
    if request.user.is_authenticated:
        # Journalisation avant dconnexion
        logger.info(f"Dconnexion de {request.user.email} depuis {request.META.get('REMOTE_ADDR')}")
        
        # Nettoyage complet de la session
        session_keys = list(request.session.keys())
        for key in session_keys:
            del request.session[key]
        
        # Dconnexion propre
        logout(request)
        
        # Prparation de la rponse
        response = redirect('/')
        
        # Suppression des cookies avec valeurs par dfaut si les paramtres sont absents
        session_cookie_name = getattr(settings, 'SESSION_COOKIE_NAME', 'sessionid')
        csrf_cookie_name = getattr(settings, 'CSRF_COOKIE_NAME', 'csrftoken')
        
        response.delete_cookie(session_cookie_name)
        response.delete_cookie(csrf_cookie_name)
        
        # Headers de scurit
        response['Cache-Control'] = 'no-store, no-cache, must-revalidate, max-age=0'
        response['Pragma'] = 'no-cache'
        response['Expires'] = 'Fri, 01 Jan 1990 00:00:00 GMT'
        
        return response
    
    return redirect('/')

