import time

def convert_word_to_pdf(file_path):
    """Convertit un fichier Word en PDF avec Zamzar et enregistre le PDF gnr localement."""

    ZAMZAR_API_KEY = "408eb9b83748cda610d5430a42d6c2b5c6e9eba9"
    ZAMZAR_ENDPOINT = "https://api.zamzar.com/v1/jobs"

    headers = {
        "Authorization": "Bearer " + ZAMZAR_API_KEY,
    }
    files = {
        "source_file": open(file_path, "rb"),
    }
    data = {
        "target_format": "pdf",
    }

    response = requests.post(ZAMZAR_ENDPOINT, headers=headers, files=files, data=data)

    if response.status_code == 201:
        # Rcuprer l'ID de la tche  partir de la rponse
        job_id = response.json()["id"]

        # Attendre que la conversion soit termine
        while True:
            job_response = requests.get(f"https://api.zamzar.com/v1/jobs/{job_id}", headers=headers)
            if job_response.json()["status"] == "successful":
                download_url = None
                for target_file in job_response.json()["target_files"]:
                    if target_file["format"] == "pdf":
                        download_url = target_file["download_url"]
                        break
                if download_url:
                    # Tlcharger le fichier PDF localement
                    local_filename = "C://nom_du_fichier.pdf"  # Spcifiez le chemin et le nom du fichier local
                    with requests.get(download_url, stream=True) as r:
                        r.raise_for_status()
                        with open(local_filename, 'wb') as f:
                            for chunk in r.iter_content(chunk_size=8192):
                                if chunk:
                                    f.write(chunk)
                    return "Conversion russie. Le fichier PDF a t enregistr localement sur le disque C."
                else:
                    raise Exception("Aucun fichier PDF trouv dans la rponse de la tche de conversion.")
            elif job_response.json()["status"] == "failed":
                raise Exception("La conversion a chou.")
            else:
                time.sleep(5)  # Attendre 5 secondes avant de vrifier  nouveau le statut de la conversion
    else:
        # Erreur de conversion
        raise Exception("Erreur de conversion Zamzar: " + str(response.status_code))

