import requests
from django.shortcuts import render, redirect
from django.http import StreamingHttpResponse

def ma_vue(request):
  if request.method == "POST":
    url = request.POST["url"]

    try:
      response = requests.get(url, stream=True)  # Dfinir stream=True pour les fichiers volumineux
      response.raise_for_status()

      # Obtenir le nom du fichier  partir des en-ttes de la rponse (si disponible)
      filename = response.headers.get('Content-Disposition', None)
      if filename:
        filename = filename.split('filename=')[-1].strip('"')  # Extraire le nom du fichier de l'en-tte

      # Crer un StreamingHttpResponse pour une gestion efficace du tlchargement
      file_response = StreamingHttpResponse(response.content, content_type=response.headers['Content-Type'])
      if filename:
        # Dfinir l'en-tte Content-Disposition avec le nom du fichier extrait pour les navigateurs
        file_response['Content-Disposition'] = f'attachment; filename="{filename}"'
      return file_response

    except requests.exceptions.RequestException as e:
      # Grer les erreurs de tlchargement de manire lgante
      return render(request, "webpages/ctn_bpf/archive.html", {"error_message": str(e)})

  else:
    # Afficher le formulaire
    return render(request, "webpages/ctn_bpf/archive.html")




