def affecter_operation(request, id):  
    try:  
        operation = Operation.objects.get(id=int(id))  
        
        if request.method == 'POST':  
            responsable_id = request.POST.get('responsible')  
            informed_id = request.POST.get('informed')  
            accountable_id = request.POST.get('accountable')  
            consulted_id = request.POST.get('consulted')  
            date_debut = request.POST['date_debut']  
            date_fin = request.POST['date_fin']  
            commentaire = request.POST['commentaire']  

            # Rcuprer le personnel responsable, ajouter une vrification pour personnel responsable  
            if not responsable_id:  
                messages.error(request, 'Le responsable est requis.')  
                return redirect(request.META.get('HTTP_REFERER', '/'))  

            personnel = Personnel.objects.get(id=int(responsable_id))  
            
            # Rcuprer le personnel accountable, vrifier et grer les exceptions  
            instance_accountable = Personnel.objects.get(id=int(accountable_id)) if accountable_id else None  

            # Initialiser les instances pour informed et consulted  
            instance_informed = None  
            instance_consulted = None  
            
            # Rcuprer l'instance pour informed si fournie  
            if informed_id:  
                try:  
                    instance_informed = Personnel.objects.get(id=int(informed_id))  
                except Personnel.DoesNotExist:  
                    instance_informed = None  # Si non trouv, garder None  

            # Rcuprer l'instance pour consulted si fournie  
            if consulted_id:  
                try:  
                    instance_consulted = Personnel.objects.get(id=int(consulted_id))  
                except Personnel.DoesNotExist:  
                    instance_consulted = None  # Si non trouv, garder None   

            # Mise  jour de l'opration  
            operation.personnel = personnel  
            operation.accountable = instance_accountable  
            operation.statut = 1  
            operation.date_debut = date_debut  
            operation.date_echeance = date_fin  
            operation.m_commentaire = commentaire  

            # Si informed ou consulted est dfini, les mettre  jour  
            if instance_informed:  
                operation.informed = instance_informed  
            if instance_consulted:  
                operation.consulted = instance_consulted  
            
            operation.save()  

            # Envoi d'e-mails aux personnels  
            personnels_to_notify = {  
                'responsible': (personnel, f'Monsieur {personnel.nom}, vous avez t dsign comme responsable de l\'opration {operation.nom}.'),  
                'accountable': (instance_accountable, f'Monsieur {instance_accountable.nom}, vous avez t dsign comme accountable pour l\'opration {operation.nom}.'),  
            }  

            # Ajouter les notifications si les utilisateurs sont dfinis  
            if instance_informed:  
                personnels_to_notify['informed'] = (instance_informed, f'Monsieur {instance_informed.nom}, vous avez t dsign comme informed pour l\'opration {operation.nom}.')  
            if instance_consulted:  
                personnels_to_notify['consulted'] = (instance_consulted, f'Monsieur {instance_consulted.nom}, vous avez t dsign comme consulted pour l\'opration {operation.nom}.')  

            for role, (p, message) in personnels_to_notify.items():  
                if p and p.mail:  # Assurez-vous que l'utilisateur a une adresse e-mail  
                    send_mail(  
                        'Notification de dsignation d\'opration',  
                        message + f'\n\nDtails supplmentaires :\nDate de dbut : {date_debut}\nDate de fin : {date_fin}\nCommentaire : {commentaire}\n\nMerci.',  
                        settings.DEFAULT_FROM_EMAIL,  
                        [p.mail],  
                        fail_silently=False,  
                    )  

            messages.success(request, 'Affection russie et notifications envoyes.')  
    except Exception as e:  
        messages.error(request, f'Erreur survenue : {e}')  

    return redirect(request.META.get('HTTP_REFERER', '/'))   

