class MonFormulaireView(View):
    def get(self, request):
        template = 'webpages/ctn_bpf/configuration_admin.html'
        context = basis(request)  # Assurez-vous que `basis` est dfini
        actual_institution = context['actual_institution']
        return render(request, template, context)

    def post(self, request):
        # Crez une session pour grer les cookies
        session = requests.Session()

        # Rcuprez le token CSRF
        csrf_token = get_token(request)

        # Ajoutez le cookie CSRF  la session
        session.cookies['csrftoken'] = csrf_token

        # Rcuprez les donnes du formulaire
        nom = request.POST.get('nom')

        # Dfinissez la mutation GraphQL
        mutation = f"""
            mutation {{
                createAnnee(nom: {json.dumps(nom)}) {{
                    annee {{
                id
                nom
            }}
                }}
            }}
        """

        # Affichez la mutation pour le dbogage
        print("Mutation GraphQL:", mutation)

        # Envoyez la requte  votre API GraphQL
        try:
            response = session.post(
                'https://operaplus:8000/graphql/',
                json={'query': mutation},
                headers={
                    'Content-Type': 'application/json',
                    'X-CSRFToken': csrf_token  # Ajoutez le token CSRF dans les en-ttes
                },
                verify=False  # Dsactive la vrification SSL
            )

            # Affichez la rponse pour le dbogage
            print("Response Status Code:", response.status_code)
            print("Response Body:", response.json())

            # Traitez la rponse
            if response.status_code == 200:
                data = response.json()
                if 'errors' in data:
                    # Grer les erreurs renvoyes par GraphQL
                    messages.error(request, f"Erreur GraphQL: {data['errors']}")
                    return render(request, 'webpages/ctn_bpf/configuration_admin.html', {'errors': data['errors']})

                # Rediriger vers une page de succs ou afficher un message
                messages.success(request, 'Enregistrement russi')
                return redirect(request.META.get('HTTP_REFERER', '/'))

            # En cas d'erreur HTTP
            messages.error(request, f"Erreur HTTP: {response.status_code}")
            return render(request, 'webpages/ctn_bpf/configuration_admin.html', {'error': 'Erreur lors de la soumission du formulaire.'})

        except requests.exceptions.RequestException as e:
            # En cas d'erreur de connexion
            messages.error(request, f"Erreur de connexion: {str(e)}")
            return render(request, 'webpages/ctn_bpf/configuration_admin.html', {'error': 'Erreur de connexion au serveur.'})

class Deletes_periodes(View):  
    def post(self, request, id):  
        try:  
            # Rcuprer l'objet PeriodeOk ou renvoyer une 404 si non trouv  
            periode = get_object_or_404(PeriodeOk, id=int(id))  
            
            # Prparer la requte GraphQL pour la suppression  
            query = """  
            mutation($id: ID!) {  
                deletePeriode(id: $id) {  
                    success  
                    message  
                }  
            }  
            """  
            variables = {"id": id}  
            response = requests.post(  
                'https://localhost:8000/graphql',  
                json={'query': query, 'variables': variables}  
            )  
            response_data = response.json()  

            # Vrifiez si la rponse est un succs  
            if response_data.get('data', {}).get('deletePeriode', {}).get('success'):  
                periode.delete()  
                messages.success(request, 'Suppression Russie')  
            else:  
                raise Exception(response_data.get('errors', 'Erreur inconnue'))  

        except Exception as e:  
            messages.error(request, f'Erreur Survenue : {e}')  
        
        # Rediriger vers la page prcdente ou la page d'accueil  
        return redirect(request.META.get('HTTP_REFERER', '/'))  
